function getTimeDelta(e){return then=new Date(e),now=new Date,minutes=Math.round((now.getTime()-then.getTime())/6e4),(minutes<60?minutes+" minutes":Math.round(minutes/60)+" hours")+" ago"}function displayPosts(e,t){d3.select("#posts").selectAll("li").data(e).enter().append("li").html(function(e){return"<img class = 'profile-photo' src = '../../assets/graphics/profile-photo.png'><h2>"+(""+e.uid).substring(0,5)+"*****</h2><br><p class = 'text'>"+e.text+"</p><br><span>LAST SEEN:</span> "+getTimeDelta(e.retrieved)+" ("+e.retrieved+")<br>"})}function displayTrends(e){d3.select("#trends").selectAll("li").data(e).enter().append("li").html(function(e){return e.term+"<span>("+e.meaning+")</span>"})}function updatePosts(){$("#loading").show(),$.get("https://cs.dpccdn.net/v1/censored_posts",function(e){d3.select("#posts").selectAll("*").remove(),displayPosts(e.posts,e.lastUpdated),displayTrends(e.trends.slice(0,10)),$("#loading").hide(),$("#sidebar").css("display","block"),$("#post-column").css("display","block")})}$(document).ready(function(){updatePosts()});