function displayPosts(s,t){d3.select("#posts").selectAll("li").data(s).enter().append("li").html(function(s){return"<img class = 'profile-photo' src = '../../assets/graphics/profile-photo.png'><h2>"+(""+s.uid).substring(0,5)+"*****</h2><br><p class = 'text'>"+s.text+"</p><br><span>LAST SEEN:</span> "+s.retrieved+"<br>"})}function displayTrends(s){d3.select("#trends").selectAll("li").data(s).enter().append("li").html(function(s){return"<span>"+s.term+"</span> ("+s.meaning+")"})}function updatePosts(){$("#loading").show(),$.get("https://cs.dpccdn.net/v1/censored_posts",function(s){d3.select("#posts").selectAll("*").remove(),displayPosts(s.posts,s.lastUpdated),displayTrends(s.trends),$("#loading").hide()})}$(document).ready(function(){updatePosts()});