function displayPosts(t,s){d3.select("#posts").selectAll("li").data(t).enter().append("li").html(function(t){return"<img class = 'profile-photo' src = '../../assets/graphics/profile-photo.png'><h2>"+(""+t.uid).substring(0,5)+"*****</h2><br><p class = 'text'>"+t.text+"</p><br><span>LAST SEEN:</span> "+t.retrieved+"<br>"})}function displayTrends(t){d3.select("#trends").selectAll("li").data(t).enter().append("li").html(function(t){return t.term+" ("+t.meaning+")"})}function updatePosts(){$("#loading").show(),$.get("https://cs.dpccdn.net/v1/censored_posts",function(t){d3.select("#posts").selectAll("*").remove(),displayPosts(t.posts,t.lastUpdated),displayTrends(t.trends),$("#loading").hide()})}$(document).ready(function(){updatePosts()});